mapper.delete:

update chas_yw_yybb
set isdel = 1
where id = #{id,jdbcType=VARCHAR}

mapper.select:

select id, baqid, bbhj, bbnr, lrsj, xgsj, isdel, baqmc, sfqy, qyid, qymc
from chas_yw_yybb where isdel = 0

  and baqid = #{baqid}


  and bbhj = #{bbhj}


  and sfqy = #{sfqy}


  and qyid = #{qyid}


  and baqid in (${baqidList})
