  #{lrrSfzh,jdbcType=VARCHAR}, #{lrsj,jdbcType=TIMESTAMP}, #{xgrSfzh,jdbcType=VARCHAR}, 
  #{xgsj,jdbcType=TIMESTAMP}, #{baqid,jdbcType=VARCHAR}, #{baqmc,jdbcType=VARCHAR}, #{yjlb,jdbcType=VARCHAR}
  , #{yjsc,jdbcType=INTEGER}, #{cffs,jdbcType=VARCHAR}, #{yjjb,jdbcType=VARCHAR},#{yjfs,jdbcType=VARCHAR},#{xsajyjsj,jdbcType=VARCHAR}
  ,#{xzajyjsj,jdbcType=VARCHAR},#{xstqyjsj,jdbcType=VARCHAR},#{xztqyjsj,jdbcType=VARCHAR}
  )

mapper.select:
select ID, ISDEL, DATA_FLAG, LRR_SFZH, LRSJ, XGR_SFZH, XGSJ, BAQID, BAQMC, YJLB,yjsc,cffs,yjjb,xsajyjsj,xzajyjsj,xstqyjsj,xztqyjsj
